import{gsap}from"gsap";function heroanimation(){var a=gsap.timeline();return a.from("#header",{duration:.75,x:200,alpha:0,delay:.25}).from("#line-2",{duration:.5,x:200,alpha:0,delay:1},"-0.25").from("#button",{duration:.5,y:50,alpha:0,delay:1},"-0.25"),a}var mainTL=gsap.timeline();mainTL.add(heroanimation());let button=document.querySelector("aside");var buttonTL=gsap.timeline({paused:!0});buttonTL.to("#button",{duration:.25}).to("aside",{duration:.25,x:-800},"playSameTime").to("form",{duration:.25,scaleX:1},"playSameTime").to("aside",{duration:.25,alpha:0},"playSameTime"),button.addEventListener("mouseenter",(function(){buttonTL.play()})),button.addEventListener("mouseleave",(function(){buttonTL.reverse()}));