import{gsap}from"gsap";function heroAnimation(){var a=gsap.timeline();let t=gsap.matchMedia();return t.add("(min-width 760px)",(()=>{a.from("#header",{duration:.75,x:200,alpha:0}).from("#line-2",{duration:.5,x:200,alpha:0},"-0.25").from("#button",{duration:.5,y:50,alpha:0},"-0.25")})),t.add("(max-width 759px)",(()=>{a.from("#header",{duration:.75,y:200,alpha:0}).from("#line-2",{duration:.5,y:200,alpha:0},"same").from("#button",{duration:.5,y:50,alpha:0},"same")})),a}gsap.set("form");var mainTL=gsap.timeline();mainTL.add(heroAnimation());let button=document.querySelector("scale");var buttonTL=gsap.timeline({paused:!0});buttonTL.to("#button",{duration:.25}).to("scale",{duration:.25,scale:10},"playSameTime"),button.addEventListener("mouseenter",(function(){buttonTL.play()})),button.addEventListener("mouseleave",(function(){buttonTL.reverse()}));